import React from 'react';
import './App.css';
import { isTemplateElement } from '@babel/types';

class App extends React.Component {

  constructor(){
    super();

    this.state = {
      searchTerm: '',
      jokes: [], 
      isFetchingJoke: false
    }

    this.onTellJoke = this.onTellJoke.bind(this);
    this.onSearchChange = this.onSearchChange.bind(this);
    this.onSearchSubmit = this.onSearchSubmit.bind(this);
  }

  searchJokes(){
    this.setState({
      isFetchingJoke:true
    });

    console.log("Load Joke");
    fetch(`https://icanhazdadjoke.com/search?term=${this.state.searchTerm}`, {
      method:'GET',
      headers: {
        Accept: 'application/json'
      }
    })
    .then(response => response.json())
    .then(json => {
      const jokes = json.results;
      this.setState({
        jokes,
        isFetchingJoke: false
      });
      console.log("Jokes: ", jokes)
    })
  }

  // componentDidMount(){
  //   this.searchJokes();
  // }

  onTellJoke() {
    this.searchJokes();
  }

  onSearchChange(event){
    this.setState({
      searchTerm:event.target.value
    })
  }

  onSearchSubmit(event){
    event.preventDefault(); //Prevent reload
    console.log("Submit");
    this.searchJokes();
  }

  renderJokes(){
    return(
    <ul>
      {this.state.jokes.map(item => <li key={item.id}>{item.joke}</li>)}
    </ul>
    );
  }

  render() {
    return(
      <div>
        <form onSubmit={this.onSearchSubmit}>
          <input type="text" 
              placeholder="Enter a search term..." 
              onChange={this.onSearchChange}
              >
          </input>
          <button>search</button>

          <button onClick={this.onTellJoke} disabled={this.state.isFetchingJoke}> Tell me a Joke</button>
        </form>

          {this.state.isFetchingJoke ? 'Loading Joke ...' : this.state.jokes.map(item => <li key={item.id}>{item.joke}</li>)}


        {/* <p> isFetchingJoke: {this.state.isFetchingJoke.toString()}</p> */}
        {/* <p>searchTerm: {this.state.searchTerm}</p> */}
      </div>
    )
  };

}

export default App;
