import React from 'react';
import './App.css';

class App extends React.Component {

  constructor(){
    super();

    this.state = {
      joke: null
    }
    this.onTellJoke = this.onTellJoke.bind(this);
  }

  fetchJoke(){
    console.log("click")

    fetch('https://icanhazdadjoke.com/', {
      method:'GET',
      headers: {
        Accept: 'application/json'
      }
    })
    .then(response => response.json())
    .then(json => {
      this.setState({
        joke: json.joke
      });
    })
  }

  componentDidMount(){
    this.fetchJoke();
  }



  onTellJoke() {
    this.fetchJoke();
  }

  render() {
    return(
      <div>
        <button onClick={this.onTellJoke} >Tell me a Joke</button>
        <p>{this.state.joke}</p>
      </div>
    )
  };

}

export default App;
